shaders.vert:
#define _IS_VERT_SHADER 1

precision highp float;

uniform mat4 cc_matViewProj;

#if _USE_MODEL
  uniform mat4 cc_matWorld;
#endif

attribute vec3 a_position;
attribute lowp vec4 a_color;

#if USE_TEXTURE
  attribute mediump vec2 a_uv0;
  varying mediump vec2 v_uv0;
#endif

varying lowp vec4 v_color;

void main () {
  mat4 mvp;

  #if _USE_MODEL
    mvp = cc_matViewProj * cc_matWorld;
  #else
    mvp = cc_matViewProj;
  #endif

  #if USE_TEXTURE
    v_uv0 = a_uv0;
  #endif

  v_color = a_color;

  gl_Position = mvp * vec4(a_position, 1);
}

shaders.frag:
#define _IS_FRAG_SHADER 1

precision highp float;

#if USE_TEXTURE
  uniform sampler2D texture;
  uniform sampler2D dissolveTexture;

  varying mediump vec2 v_uv0;
#endif

uniform float threshold;

#if USE_ALPHA_TEST
  uniform float alphaThreshold;
#endif

void ALPHA_TEST (in vec4 color) {
  #if USE_ALPHA_TEST
      if (color.a < alphaThreshold) discard;
  #endif
}

void ALPHA_TEST (in float alpha) {
  #if USE_ALPHA_TEST
      if (alpha < alphaThreshold) discard;
  #endif
}

varying lowp vec4 v_color;

void main () {
  vec4 color = v_color;
  float val;
  #if USE_TEXTURE
    color *= texture2D(texture, v_uv0);
    val = texture2D(dissolveTexture, v_uv0).r;
    #if _USE_ETC1_TEXTURE
      color.a *= texture2D(texture, v_uv0 + vec2(0, 0.5)).r;
    #endif
    color.a *= smoothstep(0.,val, threshold);


  #endif

  ALPHA_TEST(color);

  gl_FragColor = color;
}
